<button (click)="ping()">ping!</button><button (click)="getAll()">pong!</button>
<div *ngIf="response">
  <h1>{{response}}</h1>
</div>
<div>
  <ul>
    <li *ngFor="let entry of entries">
      {{entry.entry}} - Due:[{{entry.dueDate}}]
    </li>
  </ul>
</div>

<router-outlet></router-outlet>
import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'app-frontend';
  response='';
  entries:Entry[] =[];
  constructor(private http: HttpClient) {
    this.getAll();
  }

  ping(){
    this.http.get<Response>('http://localhost:8080/ping')
      .subscribe(data => {
        console.log(data);
        this.response=data.value;
      },
      error=>{
        console.log(error);
      });
  }
  getAll(){
    this.response='';
    this.http.get<Entry[]>('http://localhost:8080/getAll')
      .subscribe(data => {
        console.log(data);
        this.entries=data;
      },
      error=>{
        console.log(error);
      });
  }

}

interface Response {
  value: string
}
interface Entry {
  id: string,
  entry: string,
  priority: string,
  dueDate: Date
}
